SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_bordero_rec () AS $body$
DECLARE


nr_titulo_w		bigint;
vl_saldo_titulo_w	double precision;

c01 CURSOR FOR
	SELECT	b.nr_titulo,
		b.vl_saldo_titulo
	from	bordero_recebimento c,
		titulo_receber b,
		bordero_tit_rec a
	where	a.nr_titulo		= b.nr_titulo
	and	a.nr_bordero		= c.nr_bordero
	and	coalesce(c.dt_recebimento::text, '') = ''
	and	b.vl_saldo_titulo	> 0;


BEGIN

open c01;
loop
fetch c01 into	nr_titulo_w,
		vl_saldo_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	bordero_tit_rec
	set	vl_abaixar	= vl_saldo_titulo_w,
		vl_juros	= 0,
		vl_multa	= 0,
		vl_desconto	= 0
	where	nr_titulo	= nr_titulo_w;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_ajustar_bordero_rec () FROM PUBLIC;

