CREATE OR REPLACE PROCEDURE baca_ajustar_contr_dep_identif () AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_identificado_w		varchar(30) := null;
nr_ident_numerico_w		numeric(30);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_identificacao
	from	deposito_identificado
	where	coalesce(nr_ident_numerico::text, '') = ''
	and	(nr_identificacao IS NOT NULL AND nr_identificacao::text <> '');


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_identificado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_ident_numerico_w := somente_numero(nr_identificado_w);

	update	deposito_identificado
	set	nr_ident_numerico = nr_ident_numerico_w
	where	nr_sequencia	  = nr_sequencia_w;
	end;
end loop;
close C01;

commit;

end;
$body$
