CREATE OR REPLACE PROCEDURE baca_ajustar_diagnostico ( nm_usuario_p text) AS $body$
DECLARE




qt_registro_w	bigint	:= 0;

C01 CURSOR FOR
	SELECT	*
	from	diagnostico_medico;

c01_w	c01%rowtype;




BEGIN
open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	diagnostico_doenca
	set	ie_tipo_atendimento	= c01_w.ie_tipo_atendimento,
		cd_medico		= c01_w.cd_medico,
		ie_tipo_diagnostico	= c01_w.ie_tipo_diagnostico,
		dt_liberacao		= dt_atualizacao,
		ds_diagnostico		= coalesce(DS_DIAGNOSTICO,c01_w.ds_diagnostico)
	where	nr_atendimento		= c01_w.nr_atendimento
	and	dt_diagnostico		= c01_w.dt_diagnostico;

	qt_registro_w	:= qt_registro_w + 1;

	if (qt_registro_w	= 10000) then
		qt_registro_w	:= 0;
		commit;

	end if;
	end;
end loop;
close C01;

commit;

end;
