CREATE OR REPLACE PROCEDURE baca_ajustar_ecd_reg_bp ( cd_estabelecimento_p bigint, dt_fim_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_aglutinacao_w 			varchar(20);

c01 CURSOR FOR
SELECT	distinct
	a.cd_aglutinacao
from	ecd_balanco_patrimonial_v a,
	conta_contabil b
where  	a.dt_referencia		= trunc(dt_fim_p, 'mm')
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	b.cd_conta_contabil 	= a.cd_aglutinacao
and	b.ie_tipo 		= 'A';


BEGIN

open c01;
loop
fetch c01 into
	cd_aglutinacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

begin
	update	conta_contabil
	set	ie_ecd_reg_bp = 'S',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	=  nm_usuario_p
	where	cd_conta_contabil = cd_aglutinacao_w;
end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
