CREATE OR REPLACE PROCEDURE baca_ajustar_edicao_tnm () AS $body$
DECLARE


qtd_reg_ajust_w   bigint;
nr_sequencia_w    can_tnm_localizacao.nr_sequencia%type;

c01 CURSOR FOR
  SELECT nr_sequencia
  from can_tnm_localizacao
  where coalesce(cd_edicao_tnm::text, '') = '';


BEGIN
  open c01;
    loop
    fetch c01 into
      nr_sequencia_w;
    EXIT WHEN NOT FOUND; /* apply on c01 */
    begin
      update can_tnm_localizacao
      set cd_edicao_tnm = 6,
      dt_atualizacao_nrec = clock_timestamp(),
      nm_usuario_nrec = 'TASY_BACA'
      where nr_sequencia = nr_sequencia_w;
    end;
    end loop;
  close c01;

  select count(*)
  into STRICT qtd_reg_ajust_w
  from can_tnm_localizacao
  where nm_usuario_nrec = 'TASY_BACA';

  if qtd_reg_ajust_w > 0 then
    commit;
  end if;

end;
