SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_eme_usuarios () AS $body$
DECLARE


cd_pessoa_dependente_w	varchar(10);
cd_codigo_convenio_w		varchar(30);
cd_sistema_ant_w		varchar(20);

c01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		cd_codigo_convenio
	from	eme_pf_contrato;


BEGIN

open c01;
loop
fetch c01 into
	cd_pessoa_dependente_w,
	cd_codigo_convenio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (cd_pessoa_dependente_w IS NOT NULL AND cd_pessoa_dependente_w::text <> '') then
		begin
		select	cd_sistema_ant
		into STRICT	cd_sistema_ant_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_dependente_w;

		if (coalesce(cd_sistema_ant_w::text, '') = '') then
			update	pessoa_fisica
			set	cd_sistema_ant = substr(cd_codigo_convenio_w,1,20)
			where	cd_pessoa_fisica = cd_pessoa_dependente_w;
		end if;
		end;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
