SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_funcao_alt_tasy ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_alteracao_w		bigint;
cd_funcao_w			integer;
ie_tipo_cliente_w		varchar(1)	:= 'P';

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_funcao
	from	alteracao_tasy
	where	dt_alteracao > '27/05/2009';
	--ie_tipo_cliente is null;
BEGIN

open C01;
loop
fetch C01 into
	nr_seq_alteracao_w,
	cd_funcao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/* Atualizar o tipo do cliente no news */

	select	coalesce(max(ie_tipo_cliente),'P')
	into STRICT	ie_tipo_cliente_w
	from	funcao_net
	where	cd_funcao	= cd_funcao_w;

	update	alteracao_tasy
	set	ie_tipo_cliente	= ie_tipo_cliente_w
	where	nr_sequencia	= nr_seq_alteracao_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
