SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_hrs_real () AS $body$
DECLARE


C01 CURSOR FOR
	SELECT a.nr_sequencia
	from proj_cron_etapa a,
		proj_cronograma b,
		proj_projeto c
	where a.nr_seq_cronograma = b.nr_sequencia
	and b.NR_SEQ_PROJ = c.nr_sequencia
	and b.ie_situacao = 'A'
	and a.ie_fase = 'N'
	and c.IE_ORIGEM = 'P'
	and c.ie_status = 'E'
	and a.qt_hora_real <> (SELECT	round((dividir(coalesce(sum(coalesce(d.qt_min_ativ,0)),0), 60))::numeric, 2) qt_rat_ativ
						from	proj_rat_ativ d
						where	d.nr_seq_etapa_cron = a.nr_sequencia)
	and not exists (select 1
					from proj_cron_etapa e 
					where e.nr_seq_superior = a.nr_sequencia);

BEGIN

for r_c01 in C01 loop
	begin
		CALL Atualizar_horas_cronograma(r_c01.nr_sequencia);
	end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
