DECLARE



nr_seq_mat_fed_sc_w	bigint;
nr_seq_material_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_sequencia
	from	pls_material b,
		pls_mat_unimed_fed_sc a
	where	a.cd_material	= b.cd_material_ops
	and	coalesce(nr_seq_mat_uni_fed_sc::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_mat_fed_sc_w,
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_material
	set	nr_seq_mat_uni_fed_sc	= nr_seq_mat_fed_sc_w
	where	nr_sequencia		= nr_seq_material_w;

	end;
end loop;
close C01;

commit;

end;
