CREATE OR REPLACE PROCEDURE baca_ajustar_lote_fornec_nf () AS $body$
DECLARE



nr_seq_lote_w			bigint;
nr_sequencia_nf_w		bigint;
nr_item_nf_w			integer;
nr_nota_fiscal_w		numeric(20);

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_sequencia_nf,
	nr_item_nf
from	material_lote_fornec
where	(nr_sequencia_nf IS NOT NULL AND nr_sequencia_nf::text <> '')
and	(nr_item_nf IS NOT NULL AND nr_item_nf::text <> '')
and	coalesce(nr_nota_fiscal::text, '') = '';


BEGIN

OPEN C01;
LOOP
FETCH C01 INTO
	nr_seq_lote_w,
	nr_sequencia_nf_w,
	nr_item_nf_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_nota_fiscal),0)
	into STRICT	nr_nota_fiscal_w
	from	nota_fiscal_item
	where	nr_sequencia	= nr_sequencia_nf_w
	and	nr_item_nf	= nr_item_nf_w;
	if (nr_nota_fiscal_w > 0) then
		update	material_lote_fornec
		set	nr_nota_fiscal = nr_nota_fiscal_w
		where	nr_sequencia = nr_seq_lote_w;
	end if;
	end;
end loop;
close c01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
