CREATE OR REPLACE PROCEDURE baca_ajustar_movto_inventario (dt_mesano_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


cd_centro_local_w				bigint;
cd_centro_custo_w				bigint;
cd_local_estoque_w			bigint;
nr_movimento_estoque_w			bigint;

c01 CURSOR FOR
SELECT	a.nr_movimento_estoque,
	a.cd_local_estoque,
	a.cd_centro_custo
from	movimento_estoque a
where	a.dt_mesano_referencia	= dt_mesano_referencia_p
and	a.ie_origem_documento	= '5'
and	a.cd_operacao_estoque in (9,10,29,30);


BEGIN

open C01;
loop
fetch C01 into
	nr_movimento_estoque_w,
	cd_local_estoque_w,
	cd_centro_custo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(cd_centro_custo_w::text, '') = '') then

		select	max(cd_centro_custo)
		into STRICT	cd_centro_local_w
		from	local_estoque
		where	cd_local_estoque		= cd_local_estoque_w;

		update	movimento_estoque
		set	cd_centro_custo		= cd_centro_local_w
		where	nr_movimento_estoque	= nr_movimento_estoque_w;

		insert into log_tasy(
			cd_log,
			ds_log,
			nm_usuario,
			dt_atualizacao)
		values (	1029,
			'NR_MOVIMENTO_ESTOQUE=' || nr_movimento_estoque_w || ';',
			'BacaInvent',
			clock_timestamp());
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
