SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_nm_pf () AS $body$
DECLARE


cd_pessoa_fisica_w 	varchar(10);
nr_sequencia_w		bigint;
qt_controle_w		bigint;

C01 CURSOR FOR
SELECT 	cd_pessoa_fisica
from	pessoa_fisica
where	coalesce(nm_pessoa_pesquisa::text, '') = '';

C02 CURSOR FOR
SELECT 	cd_pessoa_fisica,
		nr_sequencia
from	compl_pessoa_fisica
where	coalesce(nm_contato_pesquisa::text, '') = ''
and		(nm_contato IS NOT NULL AND nm_contato::text <> '');




BEGIN

qt_controle_w :=0;

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		update	pessoa_fisica
		set		dt_atualizacao = dt_atualizacao
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		qt_controle_w := qt_controle_w + 1;

		if (qt_controle_w > 1000) then
			qt_controle_w := 0;
			commit;
		end if;

	exception
	when others then
		qt_controle_w := qt_controle_w + 1;
	end;
end loop;
close C01;

commit;

qt_controle_w := 0;

open C02;
loop
fetch C02 into
	cd_pessoa_fisica_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
		update	compl_pessoa_fisica
		set		dt_atualizacao 		= dt_atualizacao
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_w
		and		nr_sequencia		= nr_sequencia_w;

		qt_controle_w := qt_controle_w + 1;

		if (qt_controle_w > 1000) then
			qt_controle_w := 0;
			commit;
		end if;

	exception
	when others then
		qt_controle_w := qt_controle_w + 1;
	end;
end loop;
close C02;


commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
