SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_pendente_amostra () AS $body$
DECLARE


nr_seq_prescr_w		integer;
nr_prescricao_w		bigint;
qt_reg_w		bigint;
ds_erro_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_prescricao,
		nr_sequencia
	from	prescr_procedimento
	where	ie_pendente_amostra = 'S';


BEGIN

begin
qt_reg_w	:= 0;
open C01;
loop
fetch C01 into
	nr_prescricao_w,
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update  prescr_procedimento
	set	ie_pendente_amostra = 'N'
	where	nr_prescricao	= nr_prescricao_w
	and	nr_sequencia = nr_seq_prescr_w
	and	ie_pendente_amostra = 'S';

	qt_reg_w	:= qt_reg_w + 1;

	CALL gravar_log_tasy(8700,'Prescr: '||nr_prescricao_w||' Seq.: '||nr_seq_prescr_w,'TasyBacaPend');
	if (qt_reg_w	= 1000) then
		commit;
		qt_reg_w	:= 0;
	end if;

	end;
end loop;
close C01;

commit;
exception
	when others then
	null;
	ds_erro_w	:= SQLERRM;
	insert into log_tasy(
		dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values (	clock_timestamp(),
		'TasyBacaPend',
		8700,
		'Erro ao atualizar pendente amostra: Prescr: '|| nr_prescricao_w ||' - Seq.: '||nr_seq_prescr_w||' ds_erro_w: '||ds_erro_w);
	commit;

end;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
