CREATE OR REPLACE PROCEDURE baca_ajustar_pf_autor_cir () AS $body$
BEGIN


update	autorizacao_cirurgia a
set	a.cd_pessoa_fisica = (	SELECT 	x.cd_pessoa_fisica
				from 	atendimento_paciente x
				where	x.nr_atendimento = a.nr_atendimento)
where	coalesce(a.cd_pessoa_fisica::text, '') = ''
and	(a.nr_atendimento IS NOT NULL AND a.nr_atendimento::text <> '')
and	coalesce(a.nr_seq_agenda::text, '') = ''
and	coalesce(a.nr_seq_agenda_consulta::text, '') = '';

update	autorizacao_cirurgia a
set	a.cd_pessoa_fisica = (	SELECT 	x.cd_pessoa_fisica
				from 	agenda_paciente x
				where	x.nr_sequencia = a.nr_seq_agenda)
where	coalesce(a.cd_pessoa_fisica::text, '') = ''
and	(a.nr_seq_agenda IS NOT NULL AND a.nr_seq_agenda::text <> '')
and	coalesce(a.nr_atendimento::text, '') = '';

update	autorizacao_cirurgia a
set	a.cd_pessoa_fisica = (	SELECT 	x.cd_pessoa_fisica
				from 	autorizacao_convenio x
				where	x.nr_sequencia = a.nr_seq_autor_conv)
where	coalesce(a.cd_pessoa_fisica::text, '') = ''
and	a.nr_seq_agenda is  null
and	coalesce(a.nr_atendimento::text, '') = ''
and	(a.nr_seq_autor_conv IS NOT NULL AND a.nr_seq_autor_conv::text <> '');


update	autorizacao_cirurgia a
set	a.cd_pessoa_fisica = (	SELECT 	x.cd_pessoa_fisica
				from 	agenda_consulta x
				where	x.nr_sequencia = a.nr_seq_agenda_consulta)
where	coalesce(a.cd_pessoa_fisica::text, '') = ''
and	a.nr_seq_agenda is  null
and	coalesce(a.nr_atendimento::text, '') = ''
and	(a.nr_seq_agenda_consulta IS NOT NULL AND a.nr_seq_agenda_consulta::text <> '');


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
