CREATE OR REPLACE PROCEDURE baca_ajustar_prescr () AS $body$
DECLARE

 
nr_prescricao_w	bigint;
nr_seq_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_prescricao,	 
	a.nr_sequencia 
from	prescr_material a, 
	prescr_medica b 
where	b.dt_prescricao between to_date('06/05/2010 08:10:00','dd/mm/yyyy hh24:mi:ss') and to_date('06/05/2010 11:15:00','dd/mm/yyyy hh24:mi:ss') 
and	a.ie_urgencia	= 'S' 
and	a.ie_agrupador = 2 
and	coalesce(a.cd_motivo_baixa,0) = 0 
and	a.nr_prescricao	= b.nr_prescricao 
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_prescricao_w, 
	nr_seq_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL Atualiza_prescr_mat_hor(nr_prescricao_w,nr_seq_w,0,'OSSirio','S',null,null);
	end;
end loop;
close C01;
 
 
commit;
 
end;
