CREATE OR REPLACE PROCEDURE baca_ajustar_proc_tuss_lab () AS $body$
DECLARE


nr_seq_exame_w		bigint;
ie_existe_w		bigint;
cd_procedimento_tuss_w	bigint;
qt_reg_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_exame,
		cd_procedimento_tuss
	from	exame_laboratorio
	where	coalesce(ie_origem_proc_tuss::text, '') = ''
	and	(cd_procedimento_tuss IS NOT NULL AND cd_procedimento_tuss::text <> '');


BEGIN

begin
qt_reg_w	:= 0;
open C01;
loop
fetch C01 into
	nr_seq_exame_w,
	cd_procedimento_tuss_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	ie_existe_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_tuss_w
	and	ie_origem_proced = 8;

	if (coalesce(ie_existe_w,0) > 0) then

		update  exame_laboratorio
		set	ie_origem_proc_tuss = 8
		where	nr_seq_exame	= nr_seq_exame_w
		and	cd_procedimento_tuss = cd_procedimento_tuss_w;

	end if;

	qt_reg_w	:= qt_reg_w + 1;

	if (qt_reg_w	= 500) then
		commit;
		qt_reg_w	:= 0;
	end if;

	end;
end loop;
close C01;

commit;
exception
	when others then
	null;
end;
end;
