SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_tabelas_pepo () AS $body$
DECLARE



qt_registro_w	bigint := 0;
qt_registro_ww	bigint := 0;


BEGIN

/* Dropar as tabelas e criar novamente	*/

if (clock_timestamp() < to_date('01/11/2006','dd/mm/yyyy')) then
	select	count(*)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name	= 'CIRURGIA_AGENTE_ANEST_OCOR'
	AND	COLUMN_NAME	= 'CD_UNIDADE_MEDIDA';

	if (qt_registro_w > 0) then
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table CIRURGIA_AGENTE_ANEST_OCOR');
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table CIRURGIA_AGENTE_DISP');
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table AGENTE_ANEST_DILUICAO');
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table CIRURGIA_AGENTE_ANESTESICO');
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table AGENTE_ANEST_MATERIAL');
		commit;
	end if;

	select	count(*)
	into STRICT	qt_registro_ww
	from	user_tab_columns
	where	table_name	= 'AGENTE_ANESTESICO'
	AND	COLUMN_NAME	= 'IE_OPIOIDE';

	if (qt_registro_ww > 0) then
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop table AGENTE_ANESTESICO');
		commit;
	end if;

	if	((qt_registro_ww > 0 ) or (qt_registro_w > 0)) then
		CALL tasy_sincronizar_base();
		CALL valida_objetos_sistema();
	end if;

end if;

/* Alterar a tabela CIRURGIA_AGENTE_DISP */

if (clock_timestamp() < to_date('01/12/2006','dd/mm/yyyy')) then
	select	count(*)
	into STRICT	qt_registro_w
	from	user_tab_columns
	where	table_name	= 'CIRURGIA_AGENTE_DISP'
	AND	COLUMN_NAME	= 'NR_SEQ_AGENTE_CIR';

	if (qt_registro_w > 0) then
		CALL Exec_sql_Dinamico('Dalcastagne', 'delete from CIRURGIA_AGENTE_DISP');
		CALL Exec_sql_Dinamico('Dalcastagne', 'drop index CIRAGDIS_CIAGANE_FK_I');
		CALL Exec_sql_Dinamico('Dalcastagne', 'alter table CIRURGIA_AGENTE_DISP drop column NR_SEQ_AGENTE_CIR');
		commit;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
