CREATE OR REPLACE PROCEDURE baca_ajustar_taxa_item_conta ( nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w		bigint;
nr_sequencia_w			bigint;
tx_material_w			double precision;
nr_seq_taxa_item_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		tx_material
	from	pls_regra_preco_tx_mat;


BEGIN

select	coalesce(max(cd_estabelecimento),1)
into STRICT	cd_estabelecimento_w
from	pls_outorgante;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	tx_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_taxa_item_w
	from	pls_taxa_item
	where	tx_item	= tx_material_w;

	if (nr_seq_taxa_item_w = 0) then
		select	nextval('pls_taxa_item_seq')
		into STRICT	nr_seq_taxa_item_w
		;

		insert into pls_taxa_item(nr_sequencia, ds_taxa, cd_estabelecimento,
			tx_item, ie_situacao, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec)
		values (	nr_seq_taxa_item_w, tx_material_w, cd_estabelecimento_w,
			tx_material_w, 'A', clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p);
	end if;

	update	pls_regra_preco_tx_mat
	set	nr_seq_taxa_item	= nr_seq_taxa_item_w
	where	nr_sequencia		= nr_sequencia_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
