CREATE OR REPLACE PROCEDURE baca_ajustar_tipo_cobr ( nm_usuario_p text) AS $body$
DECLARE


ie_tipo_cobranca_w	varchar(255);
nr_seq_fatura_w		bigint;

C01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ie_tipo_cobranca
from	pls_fatura_conta a;


BEGIN

begin
open C01;
loop
fetch C01 into
	nr_seq_fatura_w,
	ie_tipo_cobranca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	update	pls_fatura_proc
	set	ie_tipo_cobranca	= ie_tipo_cobranca_w
	where	nr_seq_fatura_conta	= nr_seq_fatura_w
	and	coalesce(ie_tipo_cobranca::text, '') = '';

	update	pls_fatura_mat
	set	ie_tipo_cobranca	= ie_tipo_cobranca_w
	where	nr_seq_fatura_conta	= nr_seq_fatura_w
	and	coalesce(ie_tipo_cobranca::text, '') = '';

	commit;

end loop;
close C01;
exception when others then
	null;
end;

end;
