SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_tipo_localizar () AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_tipo_restricao_w	smallint;
cd_cgc_w		varchar(14);

C01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ie_tipo_restricao
from	tipo_localizar_atributo a,
	tipo_localizar_atrib_valor b
where	a.nr_sequencia = b.nr_seq_atributo
and	a.ie_tipo_restricao <> b.ie_tipo_restricao;


BEGIN
Select	cd_cgc
into STRICT	cd_cgc_w
from	estabelecimento
where	cd_estabelecimento = 1;

Open C01;
Loop
	Fetch C01 into	nr_sequencia_w,
			ie_tipo_restricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	if (cd_cgc_w <> '46374500016430') then
		update	tipo_localizar_atrib_valor
		set	ie_tipo_restricao = ie_tipo_restricao_w
		where	nr_seq_atributo = nr_sequencia_w;
	else
		update	tipo_localizar_atrib_valor
		set	ie_tipo_restricao = ie_tipo_restricao_w
		where	nr_seq_atributo = nr_sequencia_w
		and	dt_atualizacao <= to_date('22/06/2010', 'dd/mm/yyyy');
	end if;

end loop;
Close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
