SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_valor (nr_seq_contrato_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	(nr_seq_titular IS NOT NULL AND nr_seq_titular::text <> '')
	order by nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	pls_segurado_preco
	set	vl_preco_ant	= 0,
		vl_preco_atual	= 0,
		vl_desconto	= 0,
		ds_observacao	= 'Valores gerados somente para titulares do contrato'
	where	nr_seq_segurado	= nr_seq_segurado_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
