CREATE OR REPLACE PROCEDURE baca_ajusta_compl_pf () AS $body$
DECLARE


cd_pessoa_fisica_w		bigint;
nm_contato_w			varchar(60);
nr_sequencia_compl_w		bigint;

c01 CURSOR FOR
SELECT	distinct
	a.cd_pessoa_fisica,
	a.nm_contato
from	compl_pessoa_fisica a
where	(a.nm_contato IS NOT NULL AND a.nm_contato::text <> '')
and	a.ie_tipo_complemento = 1
and not exists (
	SELECT	1
	from	compl_pessoa_fisica x
	where	a.cd_pessoa_fisica = x.cd_pessoa_fisica
	and	ie_tipo_complemento = 5);


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	nm_contato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select (coalesce(max(nr_sequencia),0)+1)
	into STRICT 	nr_sequencia_compl_w
	from 	compl_pessoa_fisica
	where 	cd_pessoa_fisica = cd_pessoa_fisica_w;

	insert into compl_pessoa_fisica(
				cd_pessoa_fisica,
				nr_sequencia,
				ie_tipo_complemento,
				dt_atualizacao,
				nm_usuario,
				nm_contato,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_nf_correio)
			values (	cd_pessoa_fisica_w,
				nr_sequencia_compl_w,
				5,
				clock_timestamp(),
				'tasy_imp_pf',
				nm_contato_w,
				clock_timestamp(),
				'tasy_imp_pf',
				'N');


	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
