SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajusta_cor_perfil () AS $body$
DECLARE


nr_seq_legenda_antig_w		bigint;
nr_seq_legenda_nova_w		bigint;
nr_seq_legenda_perf_w		bigint;
ds_erro_w			varchar(4000);
qt_reg_antigo_w			bigint;
qt_reg_novo_w			bigint;

C01 CURSOR FOR
	SELECT	nr_seq_padrao,
		CASE WHEN nr_seq_padrao=48 THEN 2660 WHEN nr_seq_padrao=--prescrito
		1381 THEN 2661 WHEN nr_seq_padrao=--preparo
		319 THEN 2662 WHEN nr_seq_padrao=--em exame
		49 THEN 2663 WHEN nr_seq_padrao=--executado
		141 THEN 2664 WHEN nr_seq_padrao=--laudo ditado
		146 THEN 2665 WHEN nr_seq_padrao=--laudo em digitação
		84 THEN 2666 WHEN nr_seq_padrao=--aguarda 2º aprov
		52 THEN 2667 WHEN nr_seq_padrao=--laudo liberado
		1993 THEN 2668 WHEN nr_seq_padrao=--em revisão
		1237 THEN 2669 WHEN nr_seq_padrao=--em conferência
		143 THEN 2670 WHEN nr_seq_padrao=--envelopado
		53 THEN 2671 WHEN nr_seq_padrao=--central laudos
		54 THEN 2672 WHEN nr_seq_padrao=--paciente
		55 THEN 2674 WHEN nr_seq_padrao=--setor
		56 THEN 2675 WHEN nr_seq_padrao=--médico
		57 THEN 2676 WHEN nr_seq_padrao=--same
		58 THEN 2677 WHEN nr_seq_padrao=--outros--
		47 THEN 2678 WHEN nr_seq_padrao=--pendente autoriz--
		498 THEN 2679 WHEN nr_seq_padrao=--antecipar result--
		1268 THEN 2680 WHEN nr_seq_padrao=--baixa especial
		2434 THEN 2681 WHEN nr_seq_padrao=--descartado
		2214 THEN 2682 WHEN nr_seq_padrao=--paciente isolado--
		2527 THEN 2684 WHEN nr_seq_padrao=--entregue sem laudo
		2228 THEN 2685 WHEN nr_seq_padrao=--proc. bloqueado
		51 THEN 2686 WHEN nr_seq_padrao=--laudo sem liberação
		1983 THEN 2687 --nao liberado--
		 END ,
		nr_sequencia
	from	tasy_padrao_cor_perfil
	where	nr_seq_padrao in (48,--prescrito
		1381,--preparo
		319,--em exame
		49,--executado
		141,--laudo ditado
		146,--laudo em digitação
		84,--aguarda 2º aprov
		52,--laudo liberado
		1993,--em revisão
		1237,--em conferência
		143,--envelopado
		53,--central laudos
		54,--paciente
		55,--setor
		56,--médico
		57,--same
		58,--outros--
		47,--pendente autoriz--
		498,--antecipar result--
		1268,--baixa especial
		2434,--descartado
		2214,--paciente isolado--
		2527,--entregue sem laudo
		2228,--proc. bloqueado
		51,--laudo sem liberação
		1983 --nao liberado--
		);


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_seq_legenda_antig_w,
	nr_seq_legenda_nova_w,
	nr_seq_legenda_perf_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	begin

	select	count(*)
	into STRICT	qt_reg_antigo_w
	from	tasy_padrao_cor_perfil
	where	nr_seq_padrao = nr_seq_legenda_antig_w;

	select	count(*)
	into STRICT	qt_reg_novo_w
	from	tasy_padrao_cor_perfil
	where	nr_seq_padrao = nr_seq_legenda_nova_w
	and	nm_usuario = 'TasyAjusteCor';

	if (qt_reg_novo_w < qt_reg_antigo_w) then
		insert into tasy_padrao_cor_perfil(
			nr_sequencia,
			cd_estabelecimento,
			cd_perfil,
			nr_seq_padrao,
			dt_atualizacao,
			nm_usuario,
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		SELECT	nextval('tasy_padrao_cor_perfil_seq'),
			cd_estabelecimento,
			cd_perfil,
			nr_seq_legenda_nova_w,
			clock_timestamp(),
			'TasyAjusteCor',
			ds_cor_fundo,
			ds_cor_fonte,
			ds_cor_selecao,
			dt_atualizacao_nrec,
			nm_usuario_nrec
		from	tasy_padrao_cor_perfil
		where	nr_seq_padrao	= nr_seq_legenda_antig_w
		and	nr_sequencia = nr_seq_legenda_perf_w;
	end if;
	exception when others then
	ds_erro_w	:= SQLERRM;
	/*insert into logxxxx_tasy(
		dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values(	sysdate,
		'TasyAjusteCor',
		9002,
		'Erro ao copiar cor: '|| nr_seq_legenda_antig_w ||' - '||' nr_seq_legenda_antig_w: '||' nr_seq_legenda_perf_w: '||nr_seq_legenda_perf_w||' - '|| ds_erro_w);*/
	end;
	end;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_ajusta_cor_perfil () FROM PUBLIC;

