SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajusta_exige_func_equip () AS $body$
DECLARE


ds_lista_equipamento_w		varchar(255);


BEGIN

select	vl_parametro
into STRICT	ds_lista_equipamento_w
from	funcao_parametro
where	cd_funcao = 299
and	nr_sequencia = 255;

if (ds_lista_equipamento_w <> '') then
	begin

	ds_lista_equipamento_w	:= replace(replace(ds_lista_equipamento_w,';',','),' ','');

	update	man_equipamento
	set	ie_exige_funcao_os = 'S'
	where	substr(obter_se_contido_char(nr_sequencia,ds_lista_equipamento_w),1,1) = 'S';

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
