CREATE OR REPLACE PROCEDURE baca_ajusta_mat_direto () AS $body$
DECLARE



nr_solic_compra_w			bigint;
nr_item_solic_compra_w			integer;
nr_cot_compra_w				bigint;
nr_item_cot_compra_w			integer;
nr_sequencia_w				bigint;
nr_ordem_compra_w			bigint;
nr_item_oci_w				integer;



c01 CURSOR FOR
SELECT	nr_solic_compra,
	nr_item_solic_compra
from	solic_compra_item
where	length(ds_material_direto) > 255;

c02 CURSOR FOR
SELECT	nr_cot_compra,
	nr_item_cot_compra
from	cot_compra_item
where	length(ds_material_direto_w) > 255;

c03 CURSOR FOR
SELECT	nr_sequencia
from	cot_compra_forn_item
where	length(ds_material_direto) > 255;

c04 CURSOR FOR
SELECT	nr_ordem_compra,
	nr_item_oci
from	ordem_compra_item
where	length(ds_material_direto) > 255;


BEGIN
/*solic_compra_item*/

open C01;
loop
fetch C01 into
	nr_solic_compra_w,
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	solic_compra_item
	set	ds_material_direto = substr(ds_material_direto,1,255)
	where	nr_solic_compra = nr_solic_compra_w
	and	nr_item_solic_compra = nr_item_solic_compra_w;
	end;
end loop;
close C01;

/*cot_compra_item*/

open C02;
loop
fetch C02 into
	nr_cot_compra_w,
	nr_item_cot_compra_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	update	cot_compra_item
	set	ds_material_direto_w = substr(ds_material_direto_w,1,255)
	where	nr_cot_compra = nr_cot_compra_w
	and	nr_item_cot_compra = nr_item_cot_compra_w;
	end;
end loop;
close C02;

/*cot_compra_forn_item*/

open C03;
loop
fetch C03 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin
	update	cot_compra_forn_item
	set	ds_material_direto = substr(ds_material_direto,1,255)
	where	nr_sequencia = nr_sequencia_w;
	end;
end loop;
close C03;

/*ordem_compra_item*/

open C04;
loop
fetch C04 into
	nr_ordem_compra_w,
	nr_item_oci_w;
EXIT WHEN NOT FOUND; /* apply on C04 */
	begin
	update	ordem_compra_item
	set	ds_material_direto = substr(ds_material_direto,1,255)
	where	nr_ordem_compra = nr_ordem_compra_w
	and	nr_item_oci = nr_item_oci_w;
	end;
end loop;
close C04;


commit;

CALL Exec_sql_Dinamico_bv('SOLIC_COMPRA_ITEM','ALTER TABLE SOLIC_COMPRA_ITEM MODIFY DS_MATERIAL_DIRETO VARCHAR2(255)','');
CALL Exec_sql_Dinamico_bv('COT_COMPRA_ITEM','ALTER TABLE COT_COMPRA_ITEM MODIFY DS_MATERIAL_DIRETO_W VARCHAR2(255)','');
CALL Exec_sql_Dinamico_bv('COT_COMPRA_FORN_ITEM','ALTER TABLE COT_COMPRA_FORN_ITEM MODIFY DS_MATERIAL_DIRETO VARCHAR2(255)','');
CALL Exec_sql_Dinamico_bv('ORDEM_COMPRA_ITEM','ALTER TABLE ORDEM_COMPRA_ITEM MODIFY DS_MATERIAL_DIRETO VARCHAR2(255)','');

end;
