SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajusta_ordem_aprovacao ( ie_parametro_p text) AS $body$
DECLARE

 
nr_ordem_compra_w		bigint;
ds_log_w			varchar(255);
qt_existe_nota_w		bigint;
ds_ajuste_w			varchar(255);

c01 CURSOR FOR 
	SELECT	distinct a.nr_ordem_compra 
	from	ordem_compra_item b, 
		ordem_compra a 
	where	a.nr_ordem_compra = b.nr_ordem_compra 
	and	a.dt_ordem_compra > (clock_timestamp() - interval '10 days') 
	and	(a.dt_aprovacao IS NOT NULL AND a.dt_aprovacao::text <> '') 
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
	and exists ( 
		SELECT	1 
		from	processo_aprov_compra x 
		where	x.nr_sequencia = b.nr_seq_aprovacao 
		and	x.ie_aprov_reprov = 'P');


BEGIN 
ds_ajuste_w	:= 'Somente Lido';
 
/*delete 
from	log__tasy 
where	cd_log = 3006 
and	ds_log like '%' || ds_ajuste_w;*/
 
 
open C01;
loop 
fetch C01 into 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	ds_log_w		:= ' - Sem nota';
	select	count(*) 
	into STRICT	qt_existe_nota_w 
	from	ordem_compra_nota_fiscal 
	where	nr_ordem_compra = nr_ordem_compra_w;
	if (qt_existe_nota_w > 0) then 
		ds_log_w	:= ' - Com nota fiscal';
	end if;
 
	if (ie_parametro_p = 41) then 
		ds_ajuste_w	:= 'Ajustado pelo Baca';
	end if;
 
	/*insert into log__tasy( 
		dt_atualizacao, 
		nm_usuario, 
		cd_log, 
		ds_log) 
	values( sysdate, 
		'BacaOrdem', 
		3006, 
		nr_ordem_compra_w || ds_log_w || ' - ' || ds_ajuste_w);*/
 
 
	if (ie_parametro_p = 41) then 
		update	ordem_compra 
		set	dt_aprovacao  = NULL, 
			nm_usuario = 'BacaOrdem' 
		where	nr_ordem_compra = nr_ordem_compra_w;
	end if;
	end;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_ajusta_ordem_aprovacao ( ie_parametro_p text) FROM PUBLIC;

