CREATE OR REPLACE PROCEDURE baca_ajusta_regra_loc_laudo () AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_nulo_w		bigint;
ds_retorno_w		varchar(10);


BEGIN

update	regra_loc_laudo
set	nr_sequencia	= nextval('regra_loc_laudo_seq')
where	coalesce(nr_sequencia::text, '') = '';

ds_retorno_w := Executar_SQL_Dinamico(' ALTER TABLE REGRA_LOC_LAUDO
			      DROP CONSTRAINT REGLOLA_PK ', ds_retorno_w);

ds_retorno_w := Executar_SQL_Dinamico(' ALTER TABLE REGRA_LOC_LAUDO ADD
			(
		      CONSTRAINT REGLOLA_PK   Primary Key  (
               		NR_SEQUENCIA)) ', ds_retorno_w);

select	count(*)
into STRICT	qt_nulo_w
from 	regra_loc_laudo
where	coalesce(nr_sequencia::text, '') = '';

if (qt_nulo_w = 0) then
	begin

	ds_retorno_w := Executar_SQL_Dinamico(' ALTER TABLE REGRA_LOC_LAUDO MODIFY (NR_SEQUENCIA NOT NULL) ', ds_retorno_w);

	end;
end if;

commit;

end;
$body$
