nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
cd_cnes_w	varchar(20);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_pessoa_fisica,
	a.cd_cgc
from	cnes_identificacao a;


