		select	cd_cnes
		into STRICT	cd_cnes_w
		from	pessoa_juridica
		where	cd_cgc	= cd_cgc_w;
	end if;

	update	cnes_identificacao
	set	cd_cnes	= cd_cnes_w
	where	nr_sequencia	= nr_sequencia_w;
	end;
end loop;
close c01;

