SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajuste_ehr () AS $body$
DECLARE



C01 CURSOR FOR
	SELECT	count(*),
		nr_seq_elemento,
		nr_seq_reg_template,
		(dt_atualizacao) dt_atualizacao
	from	ehr_reg_elemento a
	where	a.dt_atualizacao	>= clock_timestamp() - interval '45 days'
	group by nr_seq_elemento,
		 nr_seq_reg_template
	having count(*) >=2;


c01_w	 c01%rowtype;

BEGIN


CALL exec_sql_dinamico('Tasy',' create table ehr_reg_elemento_www as ( select NR_SEQUENCIA,           NR_SEQ_REG_TEMPLATE    ,
DT_ATUALIZACAO         ,
NM_USUARIO             ,
DT_ATUALIZACAO_NREC    ,
NM_USUARIO_NREC        ,
NR_SEQ_ELEMENTO        ,
DS_RESULTADO           ,
VL_RESULTADO           ,
DT_RESULTADO            ,
NR_SEQ_ELEM_CLUSTER    ,
QT_ALTURA_COMP         ,
DS_ESTATISTICA         ,
NR_SEQ_TEMP_CONTEUDO,
NR_REGISTRO_CLUSTER     from ehr_reg_elemento where nr_sequencia in ('||
	'select min (nr_sequencia) from  ehr_reg_elemento a where	a.dt_atualizacao >= sysdate - 45 group by nr_seq_elemento,
		 nr_seq_reg_template
	having count(*) >=2 ))');

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete 	from ehr_reg_elemento
	where 	nr_seq_reg_template	= c01_w.nr_seq_reg_template
	and	nr_seq_elemento = c01_w.nr_seq_reg_template
	and	dt_atualizacao	= c01_w.dt_atualizacao;



	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
