CREATE OR REPLACE PROCEDURE baca_ajuste_lib_medic () AS $body$
DECLARE


qt_col_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_col_w
from 	user_cons_columns
where 	constraint_name = 'LIBMAPA_PK'
and	column_name	= 'NR_SEQUENCIA';

if (qt_col_w	= 0) then
	begin

	CALL Exec_Sql_Dinamico('LibMedic',   ' update lib_material_paciente set nr_sequencia = lib_material_paciente_seq.nextval ' ||
				' where nr_sequencia is null ');

	CALL Exec_Sql_Dinamico('LibMedic',   ' alter table lib_material_paciente modify (nr_sequencia number(10,0) not null) ');


	CALL Exec_Sql_Dinamico('LibMedic',   ' alter table lib_material_paciente drop constraint LIBMAPA_PK ');
	CALL Exec_Sql_Dinamico('LibMedic',   ' drop index LIBMAPA_PK ');
	CALL Exec_Sql_Dinamico('LibMedic',   ' alter table lib_material_paciente add ( CONSTRAINT LIBMAPA_PK Primary Key (nr_sequencia)) ');

	end;
end if;

end;
