SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajuste_oci_just () AS $body$
DECLARE


nr_ordem_compra_w			bigint;
nr_item_doi_nfe_w			integer;
cd_tipo_divergencia_w		integer;
nr_sequencia_w			bigint;
nr_seq_justificativa_w		bigint;
nr_seq_oci_just_w			bigint;
qt_commit_w				bigint;

c01 CURSOR FOR
SELECT	nr_ordem_compra,
	nr_item_doi_nfe,
	cd_tipo_divergencia
from	divergencia_ordem_compra_item
where	coalesce(nr_sequencia::text, '') = ''
order by 1,2,3;

c02 CURSOR FOR
SELECT	nr_sequencia
from	divergencia_oci_justif
where	nr_ordem_compra = nr_ordem_compra_w
and	nr_item_doi_nfe = nr_item_doi_nfe_w
and	cd_tipo_divergencia = cd_tipo_divergencia_w;


BEGIN
qt_commit_w := 0;
open c01;
loop
fetch c01 into
	nr_ordem_compra_w,
	nr_item_doi_nfe_w,
	cd_tipo_divergencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	divergencia_ordem_compra_item;

	nr_sequencia_w := nr_sequencia_w + 1;

	update	divergencia_ordem_compra_item
	set	nr_sequencia		= nr_sequencia_w
	where	nr_ordem_compra	= nr_ordem_compra_w
	and	nr_item_doi_nfe	= nr_item_doi_nfe_w
	and	cd_tipo_divergencia	= cd_tipo_divergencia_w;
	end;

	open c02;
	loop
	fetch c02 into
		nr_seq_justificativa_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		update	divergencia_oci_justif
		set	nr_seq_oci_just	= nr_sequencia_w
		where	nr_sequencia		= nr_seq_justificativa_w;
		end;
	end loop;
	close c02;
	qt_commit_w := qt_commit_w + 1;

	if (qt_commit_w >= 5000) then
		commit;
		qt_commit_w := 0;
	end if;
end loop;
close c01;
commit;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
