DECLARE


nr_sequencia_w		bigint;
nr_seq_osmose_w		bigint;
qt_registro_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_osmose
	from	hd_analise_agua;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_osmose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_registro_w
	from	ca_osmose
	where	nr_sequencia = nr_seq_osmose_w;

	if (qt_registro_w > 0) then
		update	hd_analise_agua
		set	nr_seq_ca_osmose = nr_seq_osmose
		where	nr_sequencia = nr_sequencia_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
