CREATE OR REPLACE PROCEDURE baca_ajuste_regra_ressup () AS $body$
DECLARE

 
cd_estabelecimento_w	smallint;
ie_regra_ressup_w		varchar(15);

C01 CURSOR FOR 
	SELECT	ie_regra_ressup, 
		cd_estabelecimento 
	from	parametro_compras;


BEGIN 
 
Open C01;
Loop 
Fetch c01 into 
	ie_regra_ressup_w, 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (ie_regra_ressup_w = ' No Ponto') then 
		update	parametro_compras 
		set	ie_regra_ressup = 'No Ponto' 
		where	cd_estabelecimento = cd_estabelecimento_w;
	end if;
	end;
End Loop;
Close c01;
 
commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
