CREATE OR REPLACE PROCEDURE baca_ajuste_titulo_liq_cc (dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;
nr_seq_baixa_w		bigint;
cd_estabelecimento_w	smallint;

c01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.nr_sequencia, 
	b.cd_estabelecimento 
from	titulo_receber b, 
	titulo_receber_liq a 
where	a.nr_titulo	= b.nr_titulo 
and	trunc(a.dt_recebimento,'dd') between dt_inicial_p and dt_final_p 
and	exists (SELECT	1 
		from	titulo_rec_liq_cc x 
		where	x.nr_titulo	= a.nr_titulo 
		and	x.nr_seq_baixa	= a.nr_sequencia);


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w, 
	nr_seq_baixa_w, 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	 
	delete	from	titulo_rec_liq_cc 
	where	nr_titulo	= nr_titulo_w 
	and	nr_seq_baixa	= nr_seq_baixa_w;
 
	CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_w,null,nm_usuario_p,nr_titulo_w,nr_seq_baixa_w);
 
	commit;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
