SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_alimentar_prest_inter () AS $body$
DECLARE

 
tb_nr_seq_conta_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_prest_int_w	pls_util_cta_pck.t_number_table;

c01 CURSOR FOR 
	SELECT	a.nr_seq_prest_inter, 
		c.nr_sequencia 
	from	ptu_servico_pre_pagto b, 
		ptu_nota_cobranca a, 
		pls_conta c, 
		pls_prestador_intercambio d 
	where	b.ie_envio_recebimento = 'R' 
	and	b.ie_status in ('C', 'R') 
	and	a.nr_seq_serv_pre_pagto = b.nr_sequencia 
	and	d.nr_sequencia = a.nr_seq_prest_inter 
	and	c.nr_seq_nota_cobranca = a.nr_sequencia 
	and	c.ie_origem_conta = 'Z' 
	and	coalesce(c.nr_seq_prest_inter::text, '') = '';


BEGIN 
 
open c01;
loop 
	fetch c01 bulk collect into tb_nr_seq_prest_int_w, tb_nr_seq_conta_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_conta_w.count = 0;
	 
	forall i in tb_nr_seq_conta_w.first..tb_nr_seq_conta_w.last 
		update	pls_conta 
		set	nr_seq_prest_inter = coalesce(nr_seq_prest_inter, tb_nr_seq_prest_int_w(i)) 
		where 	nr_sequencia = tb_nr_seq_conta_w(i);
	commit;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
