SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_alimenta_grupo_ans () AS $body$
DECLARE

 
nr_seq_conselho_w		pessoa_fisica.nr_seq_conselho%type;
cd_medico_partic_w		pls_proc_participante.cd_medico%type;
ie_tipo_desp_mat_w		pls_material.ie_tipo_despesa%type;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
tb_nr_seq_grupo_ans_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_grupo_superior_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_item_w		pls_util_cta_pck.t_number_table;
nr_cont_w			integer;

c01 CURSOR(	dt_inicio_pc	timestamp, 
		dt_fim_pc	timestamp) FOR 
	SELECT	a.nr_sequencia, 
		a.cd_procedimento, 
		a.ie_origem_proced, 
		b.ie_tipo_guia, 
		b.nr_seq_tipo_atendimento, 
		b.cd_medico_executor, 
		b.ie_regime_internacao, 
		(SELECT	max(nr_seq_conselho) 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = b.cd_medico_executor) nr_seq_conselho, 
		b.cd_estabelecimento 
	from	pls_conta_proc a, 
		pls_conta b, 
		pls_protocolo_conta c 
	where	c.dt_mes_competencia between dt_inicio_pc and dt_fim_pc 
	and	a.nr_seq_conta = b.nr_sequencia 
	and	b.nr_seq_protocolo = c.nr_sequencia 
	and	coalesce(a.nr_seq_grupo_ans::text, '') = '';

c02 CURSOR(	dt_inicio_pc	timestamp, 
		dt_fim_pc	timestamp) FOR 
	SELECT	'M' ie_tipo_item, 
		a.nr_sequencia, 
		a.nr_seq_material, 
		b.ie_tipo_guia, 
		b.nr_seq_tipo_atendimento, 
		b.cd_medico_executor, 
		b.ie_regime_internacao, 
		a.ie_tipo_despesa, 
		(SELECT	max(nr_seq_conselho) 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = b.cd_medico_executor) nr_seq_conselho, 
		b.cd_estabelecimento 
	from	pls_conta_mat a, 
		pls_conta b, 
		pls_protocolo_conta c 
	where	c.dt_mes_competencia between dt_inicio_pc and dt_fim_pc 
	and	b.nr_seq_protocolo = c.nr_sequencia 
	and	a.nr_seq_conta = b.nr_sequencia 
	and	coalesce(a.nr_seq_grupo_ans::text, '') = '';
	
BEGIN 
nr_cont_w := 1;
 
for r_c01_w in c01(pls_util_pck.inicio_ano(clock_timestamp()), pls_util_pck.fim_mes(clock_timestamp())) loop 
 
	if (coalesce(r_c01_w.cd_medico_executor::text, '') = '') then 
 
		select 	max(cd_medico) 
		into STRICT	cd_medico_partic_w 
		from	pls_proc_participante 
		where	nr_seq_conta_proc = r_c01_w.nr_sequencia;
		 
		select	max(nr_seq_conselho) 
		into STRICT	nr_seq_conselho_w 
		from	pessoa_fisica 
		where	cd_pessoa_fisica = cd_medico_partic_w;
	else 
		nr_seq_conselho_w := r_c01_w.nr_seq_conselho;
	end if;
 
	tb_nr_seq_grupo_ans_w(nr_cont_w) := pls_obter_grupo_ans(r_c01_w.cd_procedimento, r_c01_w.ie_origem_proced, nr_seq_conselho_w, 
								r_c01_w.nr_seq_tipo_atendimento, r_c01_w.ie_tipo_guia, r_c01_w.ie_regime_internacao, 
								null, 'G', r_c01_w.cd_estabelecimento);
 
	select	max(nr_seq_grupo_superior) 
	into STRICT	tb_nr_seq_grupo_superior_w(nr_cont_w) 
	from	ans_grupo_despesa 
	where	nr_sequencia = tb_nr_seq_grupo_ans_w(nr_cont_w);
 
	tb_nr_seq_item_w(nr_cont_w) := r_c01_w.nr_sequencia;
 
	if (nr_cont_w >= pls_util_pck.qt_registro_transacao_w) then 
 
		forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last 
			update	pls_conta_proc 
			set	nr_seq_grupo_ans = tb_nr_seq_grupo_ans_w(i), 
				nr_seq_grupo_ans_sup = tb_nr_seq_grupo_superior_w(i) 
			where	nr_sequencia = tb_nr_seq_item_w(i);
		commit;
		 
		tb_nr_seq_grupo_ans_w.delete;
		tb_nr_seq_grupo_superior_w.delete;
		tb_nr_seq_item_w.delete;
		nr_cont_w := 1;
	else 
		nr_cont_w := nr_cont_w + 1;
	end if;
end loop;
 
if (tb_nr_seq_item_w.count > 0) then 
	forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last 
		update	pls_conta_proc 
		set	nr_seq_grupo_ans = tb_nr_seq_grupo_ans_w(i), 
			nr_seq_grupo_ans_sup = tb_nr_seq_grupo_superior_w(i) 
		where	nr_sequencia = tb_nr_seq_item_w(i);
	commit;
end if;
 
tb_nr_seq_grupo_ans_w.delete;
tb_nr_seq_grupo_superior_w.delete;
tb_nr_seq_item_w.delete;
nr_cont_w := 1;
 
for r_c02_w in c02(pls_util_pck.inicio_ano(clock_timestamp()), pls_util_pck.fim_mes(clock_timestamp())) loop 
 
	nr_seq_conselho_w := r_c02_w.nr_seq_conselho;
	 
	if (r_c02_w.nr_seq_material IS NOT NULL AND r_c02_w.nr_seq_material::text <> '') then 
 
		select	max(ie_tipo_despesa) 
		into STRICT	ie_tipo_desp_mat_w 
		from	pls_material 
		where	nr_sequencia = r_c02_w.nr_seq_material;
	else 
		ie_tipo_desp_mat_w := r_c02_w.ie_tipo_despesa;
	end if;
 
	tb_nr_seq_grupo_ans_w(nr_cont_w) := pls_obter_grupo_ans(null, null, nr_seq_conselho_w, 
								r_c02_w.nr_seq_tipo_atendimento, r_c02_w.ie_tipo_guia, r_c02_w.ie_regime_internacao, 
								ie_tipo_desp_mat_w, 'G', r_c02_w.cd_estabelecimento);
 
	select	max(nr_seq_grupo_superior) 
	into STRICT	tb_nr_seq_grupo_superior_w(nr_cont_w) 
	from	ans_grupo_despesa 
	where	nr_sequencia = tb_nr_seq_grupo_ans_w(nr_cont_w);
 
	tb_nr_seq_item_w(nr_cont_w) := r_c02_w.nr_sequencia;
 
	if (nr_cont_w >= pls_util_pck.qt_registro_transacao_w) then 
 
		forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last 
			update	pls_conta_mat 
			set	nr_seq_grupo_ans = tb_nr_seq_grupo_ans_w(i), 
				nr_seq_grupo_ans_sup = tb_nr_seq_grupo_superior_w(i) 
			where	nr_sequencia = tb_nr_seq_item_w(i);
		commit;
		 
		tb_nr_seq_grupo_ans_w.delete;
		tb_nr_seq_grupo_superior_w.delete;
		tb_nr_seq_item_w.delete;
		nr_cont_w := 1;
	else 
		nr_cont_w := nr_cont_w + 1;
	end if;
end loop;
 
if (tb_nr_seq_item_w.count > 0) then 
	forall i in tb_nr_seq_item_w.first..tb_nr_seq_item_w.last 
		update	pls_conta_mat 
		set	nr_seq_grupo_ans = tb_nr_seq_grupo_ans_w(i), 
			nr_seq_grupo_ans_sup = tb_nr_seq_grupo_superior_w(i) 
		where	nr_sequencia = tb_nr_seq_item_w(i);
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
