CREATE OR REPLACE PROCEDURE baca_alim_func_pgto_franq () AS $body$
DECLARE

 
tb_nr_seq_franq_w	pls_util_cta_pck.t_number_table;
tb_ie_func_pgto_w	pls_util_cta_pck.t_varchar2_table_5;

c01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_funcao_pagamento 
	from	pls_franq_pag;


BEGIN 
 
open c01;
loop 
	fetch c01 bulk collect into	tb_nr_seq_franq_w, tb_ie_func_pgto_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_ie_func_pgto_w.count = 0;
 
	forall i in tb_nr_seq_franq_w.first..tb_nr_seq_franq_w.last 
		update	pls_franq_pag 
		set	ie_funcao_pagamento = coalesce(tb_ie_func_pgto_w(i), '1') 
		where	nr_sequencia = tb_nr_seq_franq_w(i);
	commit;
end loop;
close c01;
 
end;
$body$
