SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atributo_pai_visao () AS $body$
DECLARE


nm_atributo_w		varchar(50);
nm_atributo_pai_w	varchar(60);
nm_tabela_w		varchar(50);
nr_seq_visao_w		bigint;

C01 CURSOR FOR
	SELECT	a.nm_atributo,
		a.nm_atributo_pai,
		a.nm_tabela
	from	tabela_atributo a
	where	(a.nm_atributo_pai IS NOT NULL AND a.nm_atributo_pai::text <> '')
	and	exists (	SELECT	1
			from	tabela_visao b,
				tabela_visao_atributo c
			where	a.nm_tabela = b.nm_tabela
			and 	b.nr_sequencia = c.nr_sequencia
			and	a.nm_atributo = c.nm_atributo);

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	tabela_visao
	where	nm_tabela = nm_tabela_w;


BEGIN

open C01;
loop
fetch C01 into
	nm_atributo_w,
	nm_atributo_pai_w,
	nm_tabela_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_visao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	tabela_visao_atributo
		set	nm_atributo_pai = nm_atributo_pai_w
		where	nm_atributo = nm_atributo_w
		and	nr_sequencia = nr_seq_visao_w;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
