SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_age_proc () AS $body$
DECLARE


nr_seq_agenda_w		bigint;
cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;

c01 CURSOR FOR
	SELECT	a.cd_procedimento,
		a.nr_sequencia
	from	agenda_paciente a
	where	a.dt_agenda > clock_timestamp() - interval '60 days'
	and	(a.nr_seq_proc_interno IS NOT NULL AND a.nr_seq_proc_interno::text <> '')
	and	not exists (SELECT 1
		from	proc_interno x
		where	x.cd_procedimento	= a.cd_procedimento
		and	x.nr_sequencia		= a.nr_seq_proc_interno);


BEGIN

open	c01;
loop
fetch	c01 into
	cd_procedimento_w,
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_proc_interno_w
	from	proc_interno
	where	cd_procedimento	= cd_procedimento_w;


	update	agenda_paciente
	set	nr_seq_proc_interno	= nr_seq_proc_interno_w
	where	nr_sequencia		= nr_seq_agenda_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualizar_age_proc () FROM PUBLIC;

