SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_centro_local (nm_usuario_p text) AS $body$
DECLARE



cd_centro_custo_w	integer;
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pat_local
	where	ie_situacao = 'A'
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(cd_centro_custo),0)
	into STRICT	cd_centro_custo_w
	from	pat_local
	where	nr_sequencia = nr_sequencia_w;

	if (cd_centro_custo_w > 0) then
		update	pat_bem
		set	cd_centro_custo	= cd_centro_custo_w,
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	cd_centro_custo	<> cd_centro_custo_w
		and	nr_seq_local	= nr_sequencia_w;
	end if;

	commit;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
