SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_classif_sup ( dt_vigencia_p timestamp) AS $body$
DECLARE



cd_conta_contabil_w		varchar(20);
cd_classif_superior_w		varchar(40);
cd_classificacao_w			varchar(40);
cd_empresa_w			bigint;
dt_inicio_vigencia_w		timestamp;
dt_fim_vigencia_w			timestamp;
dt_vigencia_w			timestamp;
qt_registro_w			bigint;

c01 CURSOR FOR
SELECT	a.cd_empresa,
	a.cd_conta_contabil,
	a.cd_classificacao,
	a.dt_inicio_vigencia,
	a.dt_fim_vigencia
from	conta_contabil a
where	coalesce(a.cd_classif_superior::text, '') = '';


BEGIN

dt_vigencia_w	:= coalesce(dt_vigencia_p, clock_timestamp());

open C01;
loop
fetch C01 into
	cd_empresa_w,
	cd_conta_contabil_w,
	cd_classificacao_w,
	dt_inicio_vigencia_w,
	dt_fim_vigencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	dt_vigencia_w		:= coalesce(dt_inicio_vigencia_w, dt_vigencia_w);
	cd_classif_superior_w	:= substr(ctb_obter_classif_conta_sup(cd_classificacao_w, dt_vigencia_w, cd_empresa_w),1,40);

	update	conta_contabil
	set	cd_classif_superior	= cd_classif_superior_w,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		='BacaClassifSup'
	where	cd_conta_contabil	= cd_conta_contabil_w;

	insert into log_tasy(
		cd_log,
		ds_log,
		nm_usuario,
		dt_atualizacao)
	values (	931,
		'CD=' || cd_conta_contabil_w || '#CLA=' || cd_classif_superior_w,
		'Tasy',
		clock_timestamp());

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
