SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_dados_conta_pac (dt_mesano_referencia_p timestamp, ie_conta_contabil_p text, ie_grupo_receita_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_estabelecimento_w			smallint;
ie_tipo_atendimento_w			smallint;
ie_classif_contabil_w			varchar(01);
ie_clinica_w				integer;
nr_interno_conta_w			bigint;
cd_setor_atendimento_w			bigint;
dt_inicial_w				timestamp;
dt_final_w				timestamp;
cd_procedimento_w			bigint;
ie_origem_proced_w			bigint;
cd_material_w				bigint;
nr_seq_proc_mat_w			bigint;
ie_proc_mat_w				varchar(01);
cd_conta_contabil_w			varchar(20);
nr_seq_grupo_rec_w			bigint;
cd_convenio_w				numeric(20);
ie_status_acerto_w			smallint;
ie_atualizou_w				varchar(01);
ie_atualiza_resumo_w			varchar(01);
qt_contador_w				bigint := 0;

c01 CURSOR FOR 
	SELECT	a.nr_interno_conta, 
		a.ie_status_acerto, 
		b.cd_estabelecimento, 
		b.ie_tipo_atendimento, 
		c.ie_classif_contabil, 
		b.ie_clinica, 
		c.cd_convenio 
	from 	Convenio c, 
		atendimento_paciente b, 
		conta_paciente a 
	where 	a.nr_atendimento 	= b.nr_atendimento 
	and	a.cd_convenio_parametro	= c.cd_convenio 
	and 	a.dt_mesano_referencia	between dt_inicial_w and dt_final_w;

 
c02 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		'P' ie_proc_mat, 
		a.cd_procedimento, 
		a.ie_origem_proced, 
		0 cd_material, 
		a.cd_setor_atendimento, 
		a.cd_conta_contabil, 
		a.nr_seq_grupo_rec 
	from	procedimento_paciente a 
	where	a.nr_interno_conta	= nr_interno_conta_w 
	
union
 
	SELECT	a.nr_sequencia, 
		'M' ie_proc_mat, 
		0, 0, a.cd_material, 
		a.cd_setor_atendimento, 
		a.cd_conta_contabil, 
		a.nr_seq_grupo_rec 
	from	material_atend_paciente a 
	where	a.nr_interno_conta	= nr_interno_conta_w;


BEGIN 
 
dt_inicial_w		:= trunc(dt_mesano_referencia_p, 'mm');
dt_final_w		:= (add_months(trunc(dt_mesano_referencia_p, 'mm'),1) + 1) - 86400/86399;
 
 
open	c01;
loop 
fetch	c01 into 
	nr_interno_conta_w, 
	ie_status_acerto_w, 
	cd_estabelecimento_w, 
	ie_tipo_atendimento_w, 
	ie_classif_contabil_w, 
	ie_clinica_w, 
	cd_convenio_w;	
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	qt_contador_w	:= qt_contador_w + 1;
 
 
	ie_atualiza_resumo_w	:= 'N';
	 
	open	c02;
	loop 
	fetch	c02 into 
		nr_seq_proc_mat_w, 
		ie_proc_mat_w, 
		cd_procedimento_w, 
		ie_origem_proced_w, 
		cd_material_w, 
		cd_setor_atendimento_w, 
		cd_conta_contabil_w, 
		nr_seq_grupo_rec_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		 
		ie_atualizou_w := Baca_Atualiza_Conta_Pac(nr_seq_proc_mat_w, ie_proc_mat_w, cd_estabelecimento_w, cd_material_w, cd_procedimento_w, ie_origem_proced_w, ie_tipo_atendimento_w, cd_setor_atendimento_w, ie_clinica_w, ie_classif_contabil_w, cd_convenio_w, cd_conta_contabil_w, nr_seq_grupo_rec_w, ie_conta_contabil_p, ie_grupo_receita_p, nm_usuario_p, ie_atualizou_w);
 
		if (ie_atualiza_resumo_w	= 'N') and (ie_atualizou_w = 'S') then 
			ie_atualiza_resumo_w	:= 'S';
		end if;
 
		end;
	end loop;	
	close	c02;
	end;
 
	if (ie_atualiza_resumo_w	= 'S') then 
		CALL atualizar_resumo_conta(nr_interno_conta_w, ie_status_acerto_w);
	end if;
	 
	if (qt_contador_w = 500) then 
		begin 
 
		qt_contador_w		:= 0;
		commit;
 
		end;
	end if;
 
end loop;
close	c01;
 
CALL gerar_eis_resultado(dt_mesano_referencia_p, nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
