SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_fases (nm_usuario_p text) AS $body$
DECLARE


nr_seq_dia_w		bigint := 0;
nr_seq_dia_fase_w	bigint := 0;
nr_seq_tumor_w		bigint;
nr_seq_fase_w		bigint;
nr_seq_tratamento_w	bigint;
dt_agenda_w		timestamp;
nr_seq_agenda_w		bigint;



C01 CURSOR FOR
	SELECT	nr_sequencia
	from	rxt_tumor
	order by nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	rxt_tratamento
	where	nr_seq_tumor = nr_seq_tumor_w
	order by nr_sequencia;

C03 CURSOR FOR
	SELECT	nr_sequencia
	from	rxt_fase_tratamento
	where	nr_seq_tratamento = nr_seq_tratamento_w
	order by nm_fase;

C04 CURSOR FOR
	SELECT 	dt_agenda,
		nr_sequencia
	from 	rxt_agenda
	where 	nr_seq_tratamento	= nr_seq_tratamento_w
	and	nr_seq_fase		= nr_seq_fase_w
	and 	ie_status_agenda <> 'C'
	order by dt_agenda;



BEGIN

open C01;
loop
fetch C01 into
	nr_seq_tumor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_tratamento_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		nr_seq_dia_w	:= 0;

		open C03;
		loop
		fetch C03 into
			nr_seq_fase_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			nr_seq_dia_fase_w := 0;

			open C04;
			loop
			fetch C04 into
				dt_agenda_w,
				nr_seq_agenda_w;
			EXIT WHEN NOT FOUND; /* apply on C04 */
				begin

				nr_seq_dia_w		:= nr_seq_dia_w + 1;
				nr_seq_dia_fase_w	:= nr_seq_dia_fase_w + 1;

				update	rxt_agenda
				set	nr_seq_dia	= nr_seq_dia_w,
					nr_seq_dia_fase	= nr_seq_dia_fase_w,
					dt_atualizacao	= clock_timestamp(),
					nm_usuario	= nm_usuario_p
				where	nr_sequencia 	= nr_seq_agenda_w;

				end;
			end loop;
			close C04;

			end;
		end loop;
		close C03;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
