SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_glosa_ret_audit (cd_convenio_p bigint) AS $body$
DECLARE


nr_interno_conta_w	bigint;
cd_autorizacao_w		varchar(20);
nr_seq_ret_item_w		bigint;
vl_historico_w		double precision;
vl_glosa_w			double precision;
vl_amenor_w			double precision;

C01 CURSOR FOR
	SELECT a.nr_interno_conta,
		 a.cd_autorizacao,
		 vl_historico
	from	hist_audit_conta_paciente d,
		lote_audit_tit_rec c,
		conta_paciente_ret_hist b,
		conta_paciente_retorno a
	where a.nr_sequencia = b.nr_seq_conpaci_ret
	  and a.cd_convenio = cd_convenio_p
	  and b.nr_seq_lote_audit = c.nr_sequencia
	  and (c.dt_baixa IS NOT NULL AND c.dt_baixa::text <> '')
	  and b.nr_seq_hist_audit = d.nr_sequencia
	  and d.ie_acao in (0,3)
	order by a.dt_inicial, b.vl_historico, d.ie_acao desc;

C02 CURSOR FOR
	SELECT	b.nr_sequencia,
			vl_glosado,
			vl_amenor
	from	convenio_retorno_item b,
		convenio_retorno a
	where a.nr_sequencia = b.nr_seq_retorno
	  and b.nr_interno_conta = nr_interno_conta_w
	  and b.cd_autorizacao = cd_autorizacao_w
	order by a.dt_baixa_cr;

BEGIN

open c01;
loop
	fetch c01 into	nr_interno_conta_w,
				cd_autorizacao_w,
				vl_historico_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	open C02;
	loop
		fetch c02 into	nr_seq_ret_item_w,
					vl_glosa_w,
					vl_amenor_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */

		if (vl_amenor_w >= vl_historico_w) then
			update Convenio_Retorno_Item
			set	vl_glosado = vl_glosado + vl_historico_w,
				vl_amenor = vl_amenor - vl_historico_w,
				ie_glosa = 'S'
			where nr_sequencia = nr_seq_ret_item_w;
			exit;
		end if;
	end loop;
	close c02;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualizar_glosa_ret_audit (cd_convenio_p bigint) FROM PUBLIC;

