SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_medida_laudo () AS $body$
DECLARE


nr_seq_medida_w				bigint;
cd_procedimento_w			bigint;
ie_origem_proced_w			bigint;
nr_seq_tipo_medida_laudo_w		bigint;
nr_seq_tipo_med_laudo_proc_w		bigint;
ds_procedimento_w			varchar(255);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_procedimento,
		ie_origem_proced
	from	medida_exame_laudo
	where	coalesce(nr_seq_tipo_medida::text, '') = ''
	order by cd_procedimento;



BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_medida_w,
	cd_procedimento_w,
	ie_origem_proced_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_seq_tipo),0)
	into STRICT	nr_seq_tipo_medida_laudo_w
	from	tipo_medida_laudo_proc
	where	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;

	if (nr_seq_tipo_medida_laudo_w = 0) then
		begin

		select	ds_procedimento
		into STRICT	ds_procedimento_w
		from	procedimento
		where	cd_procedimento		= cd_procedimento_w
		and	ie_origem_proced	= ie_origem_proced_w;

		select	nextval('tipo_medida_laudo_seq')
		into STRICT	nr_seq_tipo_medida_laudo_w
		;

		insert	into tipo_medida_laudo(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			ds_medida,
			ds_observacao)
		values (nr_seq_tipo_medida_laudo_w,
			clock_timestamp(), 'TASY',
			substr(ds_procedimento_w,1,80), null);

		select	nextval('tipo_medida_laudo_proc_seq')
		into STRICT	nr_seq_tipo_med_laudo_proc_w
		;

		insert 	into tipo_medida_laudo_proc(nr_sequencia,
			nr_seq_tipo,
			cd_procedimento,
			ie_origem_proced,
			dt_atualizacao,
			nm_usuario)
		values (nr_seq_tipo_med_laudo_proc_w,
			nr_seq_tipo_medida_laudo_w,
			cd_procedimento_w,
			ie_origem_proced_w,
			clock_timestamp(),
			'TASY');

		end;
	end if;


	if (nr_seq_tipo_medida_laudo_w <> 0) then
		begin

		update	medida_exame_laudo
		set	nr_seq_tipo_medida	= nr_seq_tipo_medida_laudo_w
		where	nr_sequencia		= nr_seq_medida_w;


		end;
	end if;
	end;
end loop;

close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
