SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_saldo_tit_rec () AS $body$
DECLARE

 
nr_titulo_w	bigint;

c01 CURSOR FOR 
SELECT	nr_titulo 
from	titulo_receber 
where	dt_emissao	>= to_date('01/01/2007','dd/mm/yyyy');


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	CALL atualizar_saldo_tit_rec(nr_titulo_w, 'TASY');
	RAISE NOTICE '%', nr_titulo_w;
	commit;
	exception 
	when others then 
		RAISE NOTICE 'ERRO';
		commit;
	end;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
