SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_turno_agenda () AS $body$
DECLARE


nr_seq_agenda_w		bigint;
hr_atual_w		timestamp;
hr_quebra_turno_w		varchar(05);
qt_min_quebra_turno_w	varchar(05);
cd_turno_w		varchar(01)	:= 0;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.hr_inicio,
		coalesce(b.hr_quebra_turno, '12'),
		coalesce(b.qt_min_quebra_turno, '00')
	from	agenda b,
		agenda_paciente a
	where	a.cd_agenda = b.cd_agenda
	and	a.dt_agenda between clock_timestamp() - interval '60 days' and clock_timestamp() + interval '60 days'
	order by	a.hr_inicio;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_seq_agenda_w,
	hr_atual_w,
	hr_quebra_turno_w,
	qt_min_quebra_turno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	cd_turno_w	:= 0;

	if ((to_char(hr_atual_w,'hh24'))::numeric  > somente_numero(hr_quebra_turno_w)) or
		(((to_char(hr_atual_w,'hh24'))::numeric  = somente_numero(hr_quebra_turno_w)) and ((to_char(hr_atual_w,'mi'))::numeric  >= somente_numero(qt_min_quebra_turno_w))) then
		cd_turno_w	:= 1;
	end if;

	update	agenda_paciente
	set	cd_turno	= cd_turno_w
	where	nr_sequencia	= nr_seq_agenda_w
	and	cd_turno	<> cd_turno_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
