SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_valor_auditoria () AS $body$
DECLARE



nr_sequencia_w	bigint;
vl_recebido_w	double precision;

c01 CURSOR FOR
SELECT 	nr_sequencia
from	conta_paciente_retorno;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select 	coalesce(sum(vl_historico),0)
	into STRICT	vl_recebido_w
	from	hist_audit_conta_paciente a,
		conta_paciente_ret_hist b
	where	a.nr_sequencia 	= b.nr_seq_hist_audit
	and	a.ie_acao	= 1
	and	b.nr_seq_conpaci_ret 	= nr_sequencia_w;

	update	conta_paciente_retorno
	set vl_recebido = vl_recebido_w
	where nr_sequencia = nr_sequencia_w;
	end;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
