SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_valor_guia (nr_seq_protocolo_p bigint) AS $body$
DECLARE

 
nr_interno_conta_w	bigint;
nr_doc_convenio_w		varchar(20);
vl_item_w			double precision;
vl_guia_w			double precision;

C01 CURSOR FOR 
	SELECT	coalesce(sum(a.vl_item),0), 
		a.nr_interno_conta, 
		coalesce(a.cd_autorizacao, 'Não Informada') 
	from	protocolo_convenio_item_v a 
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	coalesce(a.nr_seq_proc_pacote, a.nr_sequencia) = a.nr_sequencia 
	and 	coalesce(a.cd_motivo_exc_conta::text, '') = '' 
	group	by a.nr_interno_conta, 
			coalesce(a.cd_autorizacao, 'Não Informada');


BEGIN 
 
open	c01;
LOOP 
	FETCH 	c01 into vl_item_w, 
			nr_interno_conta_w, 
			nr_doc_convenio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
	select coalesce(a.vl_guia,0) 
	into STRICT	vl_guia_w 
	from	conta_paciente_guia a 
	where	a.nr_interno_conta	= nr_interno_conta_w 
	 and a.cd_autorizacao		= nr_doc_convenio_w;
 
	if (vl_item_w <> vl_guia_w) then 
		update conta_paciente_guia 
		set vl_guia = vl_item_w 
		where nr_interno_conta	= nr_interno_conta_w 
		 and cd_autorizacao	= nr_doc_convenio_w;
	end if;
 
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualizar_valor_guia (nr_seq_protocolo_p bigint) FROM PUBLIC;

